mapper.select:
SELECT CON_NO,CON_TITLE,CON_TXT
FROM CONTENTS_DATA
WHERE CON_DV = #{con_dv} AND CON_NO = #{con_no}

mapper.update:
UPDATE CONTENTS_DATA SET
READ_COUNT = READ_COUNT+1 
WHERE CON_NO = #{con_no} AND CON_DV = #{con_dv}

UPDATE CONTENTS_DATA 
	SET CON_TITLE = #{con_title}, CON_TXT = #{con_txt}, UPD_DATE = SYSDATE
		WHERE CON_DV = #{con_dv} AND CON_NO = #{con_no}

mapper.select:
SELECT CASE WHEN count(*)=1 THEN 1 ELSE 0 END
	FROM CONTENTS_DATA
		WHERE CON_DV = #{con_dv} AND CON_NO = #{con_no} AND CON_ID = #{con_id} AND CON_PASSWORD = #{con_password}

mapper.update:
UPDATE CONTENTS_DATA 
	SET DEL_YN = 'Y', UPD_DATE = SYSDATE
		WHERE CON_DV = #{con_dv} AND CON_NO = #{con_no}
